parameters.jsCode:
// Process incoming inventory data
const items = $input.all();
const processedItems = [];

for (const item of items) {
  const data = item.json;

  // Extract inventory details from webhook data
  const inventoryGroup = data.inventoryGroup || data.group || 'Default Inventory';
  const inventoryDetails = data.inventoryDetails || data.details || data.message || JSON.stringify(data);

  // Create processed item
  processedItems.push({
    json: {
      inventoryGroup: inventoryGroup,
      inventoryDetails: inventoryDetails,
      timestamp: $now,
      sourceData: data
    }
  });
}

return processedItems;

