// Process incoming inventory data
const items = $input.all();
const processedItems = [];

for (const item of items) {
  const data = item.json;

  // Extract inventory details from webhook data
  const inventoryGroup = data.inventoryGroup || 'Default Inventory';
  const inventoryDetails = data.inventoryDetails || 'No details provided';

  // Simulate Hedera transaction (for testing)
  const mockTokenId = '0.0.' + Math.floor(Math.random() * 1000000);
  const mockTransactionId = '0.0.' + Math.floor(Math.random() * 1000000) + '@' + Date.now();

  // Create processed item
  processedItems.push({
    json: {
      success: true,
      message: 'Inventory imported successfully (simulated)',
      tokenId: mockTokenId,
      transactionId: mockTransactionId,
      inventoryGroup: inventoryGroup,
      inventoryDetails: inventoryDetails,
      timestamp: $now,
      sourceData: data,
      note: 'This is a simulated response. Hedera integration will be added once node issues are resolved.'
    }
  });
}

return processedItems;

